import React, { useState, useRef } from "react"
import { Box, Plane, useTexture } from "@react-three/drei";
import { useSpring, animated } from '@react-spring/three';
import { useFrame } from '@react-three/fiber'
import {GlassMaterial} from '../../components/Materials'


const Model001 = ({ dimensions }) => {
  //const [rotation, setRotation] = useState(0);
  const [isOpen, setIsOpen] = useState(false);

  function toggleDoor() {
    setIsOpen(!isOpen);
  }

  const doorRef = useRef();

  const { rotation } = useSpring({
    rotation: open ? [-Math.PI / 2, 0, 0] : [0, 0, 0],
    config: {
      tension: 150,
      friction: 20,
      precision: 0.0001,
      easing: (t) => {
        const cx = 0.4;
        const cy = 0;
        const ax = 0.2;
        const ay = 1;

        const x = t * (2 - t);
        const y = ax * x + cx * (1 - x);

        return ay * y + cy * (1 - y);
      },
    },
  });

  const floorTexture = useTexture("./floor.jpg");
  const wallTexture = useTexture("./concrete_polished_matte_basecolor.png");

  const [width, height, depth] = dimensions;

  const doorWidth = 0.6 + width;
  const doorPositionX = (doorWidth - 0.6) / 2;

  return (
    <group dispose={null}>
      <animated.group ref={doorRef} position={[-1.46, 0.01, -0.97]} rotation={rotation}>
        {/* Glass Door */}
        <Box
          args={[doorWidth, 2, 0.008]}
          position={[doorPositionX, 1, 0]}
          material={GlassMaterial}
          onClick={toggleDoor}
          receiveShadow
          castShadow
          />


        {/* Pomello */}
        <Box
          args={[0.03, 0.03, 0.06]}
          position={[doorWidth - 0.8 / 2, 1, 0.005]}
          receiveShadow
          castShadow
        >
          <meshStandardMaterial attach="material" color="gold" />
        </Box>

        {/* Hinges */}
        <Box
          args={[0.08, 0.08, 0.03]}
          position={[-0.28, 1.8, 0.005]}
          receiveShadow
          castShadow
        >
          <meshStandardMaterial attach="material" color="silver" />
        </Box>
        <Box
          args={[0.08, 0.08, 0.03]}
          position={[-0.28, 0.2, 0.005]}
          receiveShadow
          castShadow
        >
          <meshStandardMaterial attach="material" color="silver" />
        </Box>
      </animated.group>

      {/* collone right */}
      <Box position={[doorWidth - 3.34 / 2, 1.2, -1.5]} args={[0.15, 4, 1.2]} >
      <meshStandardMaterial
          attach="material"
          color="#fff"
          map={wallTexture}
          roughnessMap={wallTexture}
          metalnessMap={wallTexture}
          roughness={0.4}
          metalness={0.6}
        />
      </Box>

      {/* collone left */}
      <Box position={[-1.9, 1.2, -1]} args={[0.2, 4, 0.2]} >
      <meshStandardMaterial
          attach="material"
          color="#fff"
          map={wallTexture}
          roughnessMap={wallTexture}
          metalnessMap={wallTexture}
          roughness={0.4}
          metalness={0.6}
        />
      </Box>

      {/* left wall */}
      <Plane
        position={[-2, 1, 0]}
        args={[16, 16]}
        rotation={[0, Math.PI / 2, 0]}
        receiveShadow
      >
        <meshStandardMaterial
          attach="material"
          color="#fff"
          map={wallTexture}
          roughnessMap={wallTexture}
          metalnessMap={wallTexture}
          roughness={0.4}
          metalness={0.6}
        />
      </Plane>

      {/* Right wall */}
      <Plane
        position={[0, 1, -2]}
        args={[16, 16]}
        rotation={[0, 0, -Math.PI / 2]}
        receiveShadow
      >
        <meshStandardMaterial
          attach="material"
          color="#fff"
          map={wallTexture}
          roughnessMap={wallTexture}
          metalnessMap={wallTexture}
          roughness={0.4}
          metalness={0.6}
        />
      </Plane>

      {/* Floor Plane */}
      <Plane args={[20, 20]} rotation={[-Math.PI / 2, 0, 0]} receiveShadow>
        <meshStandardMaterial
          attach="material"
          color="#fff"
          map={floorTexture}
          roughnessMap={wallTexture}
          metalnessMap={wallTexture}
          roughness={0.4}
          metalness={0.6}
        />
      </Plane>
    </group>
  );
};

export default Model001;
