import { Box, Plane, useTexture } from "@react-three/drei";

const Model001 = ({ dimensions }) => {
  const floorTexture = useTexture("./floor.jpg");
  const wallTexture = useTexture("./concrete_polished_matte_basecolor.png");

  const [width, height, depth] = dimensions;

  const doorWidth = 0.6 + width;
  const doorPositionX = (doorWidth - 0.6) / 2;

  return (
    <group dispose={null}>
      <group position={[-1.35, 0, -0.97]}>
        {/* Glass Door */}
        <Box
          args={[doorWidth, 2, 0.008]}
          position={[doorPositionX, 1, 0]}
          receiveShadow
          castShadow
        >
          <meshBasicMaterial attach="material" transparent opacity={0.5} />
        </Box>

        {/* Pomello */}
        <Box
          args={[0.03, 0.03, 0.06]}
          position={[doorWidth - 0.8 / 2, 1, 0.005]}
          receiveShadow
          castShadow
        >
          <meshStandardMaterial attach="material" color="gold" />
        </Box>

        {/* Hinges */}
        <Box
          args={[0.08, 0.08, 0.03]}
          position={[-0.28, 1.8, 0.005]}
          receiveShadow
          castShadow
        >
          <meshStandardMaterial attach="material" color="silver" />
        </Box>
        <Box
          args={[0.08, 0.08, 0.03]}
          position={[-0.28, 0.2, 0.005]}
          receiveShadow
          castShadow
        >
          <meshStandardMaterial attach="material" color="silver" />
        </Box>
      </group>

      {/* left wall */}
      <Plane
        position={[-2, 1, 0]}
        args={[20, 20]}
        rotation={[0, Math.PI / 2, 0]}
        receiveShadow
      >
        <meshStandardMaterial
          attach="material"
          color="#fff"
          map={wallTexture}
        />
      </Plane>

      {/* Right wall */}
      <Plane
        position={[0, 1, -2]}
        args={[20, 20]}
        rotation={[0, 0, -Math.PI / 2]}
        receiveShadow
      >
        <meshStandardMaterial
          attach="material"
          color="#fff"
          map={wallTexture}
        />
      </Plane>

      {/* Floor Plane */}
      <Plane args={[20, 20]} rotation={[-Math.PI / 2, 0, 0]} receiveShadow>
        <meshStandardMaterial
          attach="material"
          color="#fff"
          map={floorTexture}
          roughnessMap={floorTexture}
          metalnessMap={floorTexture}
          roughness={0.4}
          metalness={0.6}
        />
      </Plane>
    </group>
  );
};

export default Model001;
